// 获取上一节点传递过来的 items
const items = $input.all();

// 创建一个空字符串，用来存储合并后的文本
let text = "";

// 遍历所有的 items，并提取出所需的信息
items.forEach((item, index) => {
    // 获取当前 item 的信息
    const creator = item.json.creator;
    const title = item.json.title;
    const link = item.json.link;
    const date = item.json.pubDate

    // 按照指定格式将每个 item 的信息拼接
    text += `第${index + 1}篇帖子标题：${title} \n 第${index + 1}篇帖子作者：${creator} \n 第${index + 1}篇帖子链接：${link}\n 第${index + 1}篇帖子发布时间：${date}\n\n`;
});

// 将合并后的字符串赋值给 text 变量
return {
    text: text.trim() // 去除最后的多余换行
};

